Ext.define('Osmd.view.sprav.TabReportOrg', {
    extend: 'Ext.panel.Panel',
    alias: 'widget.tabreportorg',

    requires: [
        'Osmd.view.sprav.TabReportOrgViewModel',
        'Ext.panel.Panel',
        'Ext.toolbar.Toolbar',
        'Ext.button.Button'
    ],

    viewModel: {
        type: 'tabreportorg'
    },
    layout: 'border',
    closable: true,
    collapseFirst: false,
    defaultListenerScope: true,

    items: [
        {
            xtype: 'panel',
            flex: 1,
            region: 'center',
            minHeight: 100,
            scrollable: true,
            layout: 'fit',
            title: ''
        }
    ],
    dockedItems: [
        {
            xtype: 'toolbar',
            dock: 'top',
            minHeight: 40,
            resizable: true,
            items: [
                {
                    xtype: 'button',
                    handler: function(button, event) {
                        var rep = Ext.REPORTCONTENT;
                        //console.log(rep);
                        var win = window.open( 'blank.php','printwin');
                        win.document.write(rep);
                        win.document.close();
                        if(Ext.isIE){

                            window.print();
                            // window.close();
                        } else {

                            win.print();
                            // win.close();
                        }

                    },
                    icon: 'resources/css/images/ico/print_printer.png',
                    scale: 'large',
                    text: 'Печать',
                    tooltip: 'Печать документа'
                },
                {
                    xtype: 'button',
                    handler: function(button, event) {
                        //STORE

                        var stUser = Ext.data.StoreManager.get("StUser");

                        //LOGIN & PASSWORD & SQL

                        var values =stUser.getAt(0);
                        var login = values.get('login');
                        var password =values.get('password');
                        var sql = Ext.REPORTSQL;
                        var title= Ext.REPORTTITLE;


                        button.setParams({login:login,password:password,sql:sql,fname:title});
                    },
                    href: 'resources/php/xls/xls.php',
                    hrefTarget: 'this',
                    icon: 'resources/css/images/ico/sprav.png',
                    text: 'Exel',
                    tooltip: 'Экспорт в електронную таблицу'
                }
            ]
        }
    ],
    listeners: {
        beforeclose: 'onPanelBeforeClose'
    },

    onPanelBeforeClose: function(panel, eOpts) {
        //in use
        var me=this;
        //TAB
        var tabPnCenter = Ext.getCmp('tabPnCenter');

        var act = 0;
        Ext.Array.forEach(tabPnCenter.getTabBar().items.items, function(f)
        {
            if (f.hidden===false) {
                act = act + 1;
            }
        });

        if (act < 2) {
            tabPnCenter.setActiveTab('tabLogin');
        }

    }

});